import React, { Component } from 'react';
// import { Card } from 'react-bootstrap';
import { Doughnut,Bar} from 'react-chartjs-2';


class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                labels:["Tablet","SmartPhone"],
                datasets:[
                    {
                        label:"Poblacion",
                        data: [ props.element.smartPhone.value,props.element.tablet.value],
                        backgroundColor: [props.element.smartPhone.color, props.element.tablet.color],
                        weight:2
                    }
                ]

            }
        }
    }
    render() {
        // var element=this.props.element;
        return (
            <div className="chart">
                <Doughnut 
                    data={this.state.chartData}
                    options={{ 
                        cutoutPercentage:85,
                        title: {
                            display: true,
                            text: this.props.element.title,
                            position:"center"

                        },
                        legend: {
                            display: false
                        }
                    }}
                >
                </Doughnut>
            </div>

            
        );
    }
}
export default Element;
