import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';
import './Element.css';
import Legend from './Legend';
var numeral = require('numeral');
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                
                datasets:[
                    {
                        label: props.element.title,
                    }
                ]

            },
            total:0
        }

    }
    componentDidMount() {
        var data = [], backgroundColor = [], labels=[],total=0;
        for (let i = this.props.element.data.length-1;i>=0;i--){
            total += this.props.element.data[i].value;
            data.push(this.props.element.data[i].value);
            backgroundColor.push(this.props.element.data[i].color);
            labels.push(this.props.element.data[i].label);
        }
                
        this.setState({
            chartData: {
                labels: labels,
                datasets: [{
                    data: data,
                    backgroundColor: backgroundColor,

                }]
            },
            total:total
        });
        
    }
    render() {
        var component = this.props.element.data.map((element, i) => {
            return (
                <Col key={i}>
                    <Legend 
                        label={element.label} 
                        color={element.color} 
                        percent={(100 / this.state.total) * element.value} 
                        value={numeral(element.value).format('0,0,0,0')}
                        simbol={this.props.element.simbol}
                        ></Legend>
                </Col>

            )
        })
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={12}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false,
                                    rotation: -0.7 * Math.PI
                                }
                            }}
                        >

                        </Doughnut>
                        <div className="flotante">
                            <div className="title">{this.props.element.title.toUpperCase()}</div>  
                            <div className="value">{numeral(this.state.total).format('0,0,0,0')}</div>                          
                        </div>
                    </Col>
                </Row>
                <Row>
                    {component}
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
