import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';

import Legend from './Legend'
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                
                datasets:[
                    {
                        label: props.element.title,
                    }
                ]

            },
            labels:""
        }

    }
    componentDidMount() {
        var data = [], backgroundColor = [], labels=[];
        this.props.element.data.forEach(element => {
            data.push(element.value);
            backgroundColor.push(element.color);
            labels.push(element.label);
        });        
        this.setState({
            chartData: {
                labels: labels,
                datasets: [{
                    data: data,
                    backgroundColor: backgroundColor,

                }]
            },
            labels
        });
        
    }
    render() {
        var component = this.props.element.data.map((element, i) => {
            return (
                <Col key={this.props.element.data.lenght-i-1}>
                    <Legend title={element.label}></Legend>
                </Col>

            )
        })
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={12}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false,
                                    rotation:0
                                }
                            }}
                        >

                        </Doughnut>
                    </Col>
                </Row>
                <Row>
                    {component}
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
