import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';

import Legend from './Legend'
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                labels:["Tablet","SmartPhone"],
                datasets:[
                    {
                        label:"Poblacion",
                        data: [ props.element.smartPhone.value,props.element.tablet.value],
                        backgroundColor: [props.element.smartPhone.color, props.element.tablet.color],
                        weight:2
                    }
                ]

            }
        }
    }
    render() {
        // var element=this.props.element;
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={4}>
                        {/* <Legend title="Tablet"></Legend> */}
                    </Col>
                    <Col sm={4}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false
                                }
                            }}
                        >

                        </Doughnut>
                    </Col>
                    <Col sm={4}>
                        {/* <Legend title="SmartPhone"></Legend> */}
                    </Col>
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
