import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';

import Legend from './Legend'
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                labels:["Tablet","SmartPhone"],
                datasets:[
                    {
                        label: props.element.title,
                        data: [props.element.data[0].value, props.element.data[1].value ],
                        backgroundColor: [props.element.data[0].color, props.element.data[1].color]
                    }
                ]

            }
        }

    }
    componentDidMount() {
        var chartData = {
            labels: [],
            datasets: [{
                data: [],
                backgroundColor: [],

            }]
        }
        // var data = [this.props.element.data[0].value, this.props.element.data[1].value], backgroundColor = [], labels=[];
        this.props.element.data.forEach(element => {
            chartData.datasets.data.push(element.value);
            chartData.datasets.backgroundColor.push(element.color);
            chartData.labels.push(element.label);
        });
        console.log(chartData);
        
        this.setState({
            chartData: chartData
        });
        
    }
    render() {
        // var element=this.props.element;
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={12}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false
                                }
                            }}
                        >

                        </Doughnut>
                    </Col>
                </Row>
                <Row>
                    <Col sm={6}>
                        <Legend title="Tablet"></Legend>
                    </Col>
                    
                    <Col sm={6}>
                        <Legend title="SmartPhone"></Legend>
                    </Col>
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
