import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';

import Legend from './Legend'
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                
                datasets:[
                    {
                        label: props.element.title,
                    }
                ]

            }
        }

    }
    componentDidMount() {
        var data = [], backgroundColor = [], labels=[];
        for (let i = this.props.element.data.length;i>=0;i--){
            console.log(i);
            
            // data.push(this.props.element.data[i].value);
            // backgroundColor.push(this.props.element.data[i].color);
            // labels.push(this.props.element.data[i].label);
        }
                
        this.setState({
            chartData: {
                labels: labels,
                datasets: [{
                    data: data,
                    backgroundColor: backgroundColor,

                }]
            }
        });
        
    }
    render() {
        var component = this.props.element.data.map((element, i) => {
            return (
                <Col key={i}>
                    <Legend title={element.label}></Legend>
                </Col>

            )
        })
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={12}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false,
                                    rotation: -0.7 * Math.PI
                                }
                            }}
                        >

                        </Doughnut>
                    </Col>
                </Row>
                <Row>
                    {component}
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
