import React, { Component } from 'react';
import { Row,Col } from 'react-bootstrap';
import { Doughnut,Line} from 'react-chartjs-2';

import Legend from './Legend'
class Element extends Component {
    // element.
    constructor(props){
        super(props);
        this.state={
            chartData:{
                labels:["Tablet","SmartPhone"],
                datasets:[
                    {
                        label: props.element.title,
                        data: [props.element.data[0].value, props.element.data[1].value ],
                        backgroundColor: [props.element.data[0].color, props.element.data[1].color]
                    }
                ]

            }
        }

    }
    componentDidMount() {
        var data = [], backgroundColor = [], labels=[];
        this.props.element.data.forEach(element => {
            data.push(element.value);
            backgroundColor.push(element.color);
            labels.push(element.label);
        });        
        this.setState({
            chartData: {
                labels: labels,
                datasets: [{
                    data: data,
                    backgroundColor: backgroundColor,

                }]
            }
        });
        
    }
    render() {
        // var element=this.props.element;
        return (
            
            <div className="chart">
                <Row>
                    <Col sm={12}>
                        <Doughnut
                            data={this.state.chartData}
                            options={{
                                cutoutPercentage: 85,
                                legend: {
                                    display: false
                                }
                            }}
                        >

                        </Doughnut>
                    </Col>
                </Row>
                <Row>
                    <Col sm={6}>
                        <Legend title="Tablet"></Legend>
                    </Col>
                    
                    <Col sm={6}>
                        <Legend title="SmartPhone"></Legend>
                    </Col>
                </Row>
                
                
                
            </div>


            
        );
    }
}
export default Element;
